examples.code:
discovery.relabel "modify" {
  targets = discovery.kubernetes.pods.targets

  rule {
    source_labels = ["__meta_kubernetes_pod_label_app"]
    target_label = "app"
  }
}

discovery.uyuni "systems" {
  server = "https://uyuni.example.com"
  username = env("UYUNI_USER")
  password = env("UYUNI_PASSWORD")
  refresh_interval = "5m"
}

faro.receiver "frontend" {
  server {
    listen_address = "0.0.0.0"
    listen_port = 12347
  }

  output {
    logs = [loki.write.default.receiver]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

loki.process "json_logs" {
  forward_to = [loki.write.default.receiver]

  stage.json {
    expressions = {
      level = "level",
      message = "msg",
    }
  }

  stage.labels {
    values = {
      level = "",
    }
  }
}

loki.source.azure_event_hubs "events" {
  fully_qualified_namespace = "mynamespace.servicebus.windows.net"
  event_hubs = ["logs"]
  authentication {
    connection_string = env("AZURE_EVENT_HUB_CONNECTION_STRING")
  }

  forward_to = [loki.write.default.receiver]
}

otelcol.connector.host_info "default" {
  host_identifiers = ["host.name", "host.id"]

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.connector.spanmetrics "default" {
  histogram {
    explicit {
      buckets = ["0.1s", "0.5s", "1s", "5s", "10s"]
    }
  }

  output {
    metrics = [otelcol.exporter.prometheus.default.input]
  }
}

otelcol.exporter.loadbalancing "backends" {
  resolver {
    static {
      hostnames = ["backend1:4317", "backend2:4317"]
    }
  }
  protocol {
    otlp {
      client {}
    }
  }
}

otelcol.exporter.otlp "default" {
  client {
    endpoint = "tempo:4317"
    tls {
      insecure = true
    }
  }
}

otelcol.extension.jaeger_remote_sampling "default" {
  grpc {
    endpoint = "0.0.0.0:14250"
  }
  source {
    remote {
      endpoint = "jaeger-collector:14250"
    }
  }
}

otelcol.processor.attributes "default" {
  action {
    key = "environment"
    value = "production"
    action = "insert"
  }

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.batch "default" {
  timeout = "5s"
  send_batch_size = 8192

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.discovery "kubernetes" {
  targets = discovery.kubernetes.pods.targets

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.k8sattributes "default" {
  extract {
    metadata = ["k8s.namespace.name", "k8s.pod.name", "k8s.deployment.name"]
  }

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.memory_limiter "default" {
  check_interval = "1s"
  limit_mib = 512
  spike_limit_mib = 128

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.resource "default" {
  attributes {
    key = "service.namespace"
    value = "production"
    action = "insert"
  }

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.resourcedetection "default" {
  detectors = ["env", "system", "docker"]

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.span "default" {
  name {
    to_attributes {
      rules = ["^/api/v1/(?P<version>\\d+).*"]
    }
  }

  output {
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.tail_sampling "default" {
  policy {
    name = "sample_errors"
    type = "status_code"
    status_code {
      status_codes = ["ERROR"]
    }
  }

  output {
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.processor.transform "default" {
  error_mode = "ignore"

  trace_statements {
    context = "span"
    statements = [
      "set(status.code, 1) where attributes[\"http.status_code\"] == 200",
    ]
  }

  output {
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.receiver.jaeger "default" {
  protocols {
    grpc {
      endpoint = "0.0.0.0:14250"
    }
    thrift_http {
      endpoint = "0.0.0.0:14268"
    }
  }

  output {
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.receiver.kafka "default" {
  brokers = ["kafka:9092"]
  topic = "otlp_spans"
  protocol_version = "2.0.0"

  output {
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.receiver.otlp "default" {
  grpc {
    endpoint = "0.0.0.0:4317"
  }

  http {
    endpoint = "0.0.0.0:4318"
  }

  output {
    metrics = [otelcol.exporter.otlp.default.input]
    logs = [otelcol.exporter.otlp.default.input]
    traces = [otelcol.exporter.otlp.default.input]
  }
}

otelcol.receiver.vcenter "default" {
  endpoint = "https://vcenter.example.com"
  username = env("VCENTER_USER")
  password = env("VCENTER_PASSWORD")

  output {
    metrics = [otelcol.exporter.otlp.default.input]
  }
}

prometheus.exporter.azure "default" {
  subscriptions = ["00000000-0000-0000-0000-000000000000"]
  resource_type = "Microsoft.Compute/virtualMachines"
}

prometheus.scrape "azure" {
  targets = prometheus.exporter.azure.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.blackbox "default" {
  config = "{\"modules\":{\"http_2xx\":{\"prober\":\"http\"}}}"target {
    name = "example"
    address = "https://example.com"
    module = "http_2xx"
  }
}

prometheus.scrape "blackbox" {
  targets = prometheus.exporter.blackbox.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.cloudwatch "default" {
  sts_region = "us-east-1"

  discovery {
    type = "AWS/EC2"
    regions = ["us-east-1"]
    search_tags = {
      env = "prod",
    }
  }
}

prometheus.scrape "cloudwatch" {
  targets = prometheus.exporter.cloudwatch.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.gcp "default" {
  project_ids = ["my-project"]
  metrics_prefixes = ["compute.googleapis.com"]
}

prometheus.scrape "gcp" {
  targets = prometheus.exporter.gcp.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.github "default" {
  api_token = env("GITHUB_TOKEN")
  repositories = ["grafana/alloy"]
}

prometheus.scrape "github" {
  targets = prometheus.exporter.github.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.kafka "default" {
  kafka_uris = ["kafka:9092"]
}

prometheus.scrape "kafka" {
  targets = prometheus.exporter.kafka.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.mssql "default" {
  connection_string = "sqlserver://user:password@localhost:1433"
}

prometheus.scrape "mssql" {
  targets = prometheus.exporter.mssql.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.mysql "default" {
  data_source_name = "root:password@(localhost:3306)/"
}

prometheus.scrape "mysql" {
  targets = prometheus.exporter.mysql.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.process "default" {
  matcher {
    name = "{{.ExeBase}}"
    cmdline = [".*"]
  }
}

prometheus.scrape "process" {
  targets = prometheus.exporter.process.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.redis "default" {
  redis_addr = "localhost:6379"
}

prometheus.scrape "redis" {
  targets = prometheus.exporter.redis.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.snmp "default" {
  config = "{\"modules\":{\"default\":{\"walk\":[\"1.3.6.1.2.1\"]}}}"

  target "router" {
    address = "192.168.1.1"
    module = "default"
  }
}

prometheus.scrape "snmp" {
  targets = prometheus.exporter.snmp.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.snowflake "default" {
  account_name = "myaccount"
  username = env("SNOWFLAKE_USER")
  password = env("SNOWFLAKE_PASSWORD")
  warehouse = "COMPUTE_WH"
}

prometheus.scrape "snowflake" {
  targets = prometheus.exporter.snowflake.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.squid "default" {
  address = "localhost:3128"
}

prometheus.scrape "squid" {
  targets = prometheus.exporter.squid.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.unix "default" {
  enable_collectors = ["cpu", "mem", "disk", "filesystem"]
}

prometheus.scrape "system" {
  targets = prometheus.exporter.unix.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

prometheus.exporter.vsphere "default" {
  vsphere_url = "https://vcenter.example.com/sdk"
  vsphere_user = env("VSPHERE_USER")
  vsphere_password = env("VSPHERE_PASSWORD")
}

prometheus.scrape "vsphere" {
  targets = prometheus.exporter.vsphere.default.targets
  forward_to = [prometheus.remote_write.default.receiver]
}

remote.vault "secrets" {
  address = "https://vault.example.com"
  path = "secret/data/alloy"

  auth {
    method = "token"
    token = env("VAULT_TOKEN")
  }
}
